@prefix fso: <https://w3id.org/fso#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix fso4b: <https://w3id.org/fso4brew#> .
@prefix qudt: <http://qudt.org/schema/qudt/> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix ex: <https://example.com/> .
@prefix inst: <https://niras.dk/xx/> .

# TBOX
ex:currentUpcyclingPercentage a owl:DatatypeProperty ;
	rdfs:label "current uplcycling percentage"@en ;
	qudt:ucumCode "%" .

ex:bottlesPerHour a owl:DatatypeProperty ;
	rdfs:label "bottles per hour"@en ;
	qudt:ucumCode "h.-1" .

ex:totalWaterConsumption a owl:DatatypeProperty ;
	rdfs:label "total water consumption"@en ;
	qudt:ucumCode "m3/h" .

ex:upcycledWaterConsumption a owl:DatatypeProperty ;
	rdfs:label "upcycled water consumption"@en ;
	qudt:ucumCode "m3/h" .

ex:totalElectricityConsumption a owl:DatatypeProperty ;
	rdfs:label "total elctricity consumption"@en ;
	qudt:ucumCode "kW" .

# ABOX
inst:rbl_a a fso4b:ReturnableBottleLine , fso:System ;
	rdfs:label "returnable bottle line for plant A"@en ;
	fso:hasSourceComponet inst:ws_a , inst:ucp_1 ;
	ex:bottlesPerHour 10000 ;
	ex:totalWaterConsumption 500 ;
	ex:upcycledWaterConsumption 100 .

inst:ws_a a fso:System , fso:Source ;
	rdfs:label "water supply for plant A"@en ;
	ex:totalWaterConsumption 400 .

inst:es_a a fso:System , fso:Source ;
	rdfs:label "electircity supply for plant A"@en ;
	ex:totalElectricityConsumption 200 .

inst:ucp_1 a fso:System , fso4b:UpcyclingProcess , fso:TreatmentDevice ;
	rdfs:label "water upcycling process 1 for plant A"@en ;
	fso:hasSourceComponet inst:rbl_a , inst:ws_a ;
	fso:hasConsumerComponent inst:rbl_a ;
	ex:currentUpcyclingPercentage 80 ;
	ex:currentSourceQuality 0.4 ;
	ex:requiredSourceQuality 0.3 ;
	ex:currentExitQuality 0.8 .

tabs.query:
PREFIX fso: <https://w3id.org/fso#>
PREFIX fso4b: <https://w3id.org/fso4brew#>
PREFIX ex: <https://example.com/>

SELECT ?rbl ?rblConsumption ?sourceConsumption
WHERE {
	?rbl a fso4b:ReturnableBottleLine ;
           ex:totalConsumption ?rblConsumption ;
           fso:hasSourceComponet ?sc .
  	?sc a fso:Source ;
          ex:totalConsumption ?sourceConsumption .
}

