PREFIX fso: <https://w3id.org/fso#>
PREFIX fso4b: <https://w3id.org/fso4brew#>
PREFIX ex: <https://example.com/>

SELECT ?rbl ?waterPerBottle
WHERE {
	?rbl a fso4b:ReturnableBottleLine ;
           ex:bottlesPerHour ?bph ;
           fso:hasSourceComponet ?sc .
  	?sc a fso:Source ;
          ex:totalWaterConsumption ?sourceConsumption .
    BIND(?sourceConsumption / ?bph AS ?waterPerBottle)
}

